      $warna = '#ffffff';
    }elseif($pt->status == '1'){
      $warna = '#FFF693';
    }elseif($pt->status == '2'){
      $warna = '#3DBEF0';
    }elseif($pt->status == '3'){
      $warna = '#EE7B57';
    }

    if($pt->jenis_map == 'polygon'){
      echo '<a href="#bawah" onclick="add('.$pt->id_kavling.')">
